import React, { useState } from 'react'
import { Text, View, StyleSheet } from "react-native"
import TextBox from "../components/TextBox"
import Btn from "../components/Btn"
import firebase from 'firebase/app';
import "firebase/auth";
import "firebase/firestore";


const styles = StyleSheet.create({
    view: {
        flex: 1,
        width: "100%",
        justifyContent: "center",
        alignItems: "center"
    }
})

export default function Loginscreen({ navigation }) {

    const [values, setValues] = useState({
        email: "",
        pwd: ""
    })

    function handleChange(text, eventName) {
        setValues(prev => {
            return {
                ...prev,
                [eventName]: text
            }
        })
    }

    function Login() {

        const { email, pwd } = values
        const UID = firebase.auth().signInWithEmailAndPassword(email, pwd)
        
        
        
            .then(() => {

            })
            .catch((error) => {
                alert(error.message)
                // ..
            });
    }

    async function getUid() {
        const user = await firebase.auth().currentUser
        if (user === null){
            return null
        }else{
            return user.uid;
        } 

    }

    async function imprima(){
        const uid = await this.getUid()
        console.log(uid);
    }

    return <View style={styles.view}>
        <Text style={{ fontSize: 34, fontWeight: "800", marginBottom: 20 }}>INICIAR SESION</Text>
        <TextBox placeholder="CORREO ELECTRONICO" onChangeText={text => handleChange(text, "email")} />
        <TextBox placeholder="CONTRASEÑA" onChangeText={text => handleChange(text, "pwd")} secureTextEntry={true} />
        <View style={{ flexDirection: "row", justifyContent: "space-between", alignItems: "center", width: "92%", }}>
            <Btn onClick={() => Login()} title="Iniciar sesion" style={{ width: "48%" }} />
            <Btn onClick={() => imprima()} title="Prueba UID" style={{ width: "48%" }} />
            <Btn onClick={() => navigation.navigate("Sign Up")} title="Registrarse" style={{ width: "48%", backgroundColor: "#344869" }} />
        </View>
    </View>
}
