import React, { useState } from 'react'
import { Text, View, StyleSheet } from "react-native"
import TextBox from "../components/TextBox"
import Btn from "../components/Btn"
import firebase from 'firebase/app';
import "firebase/auth";
import "firebase/firestore";
import database from '@react-native-firebase/database';
import "firebase/database";






const styles = StyleSheet.create({
    view: {
        flex: 1,
        width: "100%",
        justifyContent: "center",
        alignItems: "center"
    }
})

export default function Apiscreen({ navigation}) {

    const [values, setValues] = useState({
     
        api_publica: "",
        api_secreta: ""
    })

    function handleChange(text, eventName) {
        setValues(prev => {
            return {
                ...prev,
                [eventName]: text
            }
        })
    }


    async function getUid() {
        const user = await firebase.auth().currentUser
        if (user === null){
            return null
        }else{
            return user.uid;
        } 
    
    }
    
    async function Keys(){
        
        const  uid = await getUid()
        console.log(uid);
        const { api_publica, api_secreta } = values
        firebase.firestore().collection('Users').add(values).set({
            values
        })

    }

    

    return <View style={styles.view}>
        <Text style={{ fontSize: 34, fontWeight: "800", marginBottom: 20 }}>REGISTRAR KEYS</Text>
        <TextBox placeholder="INGRESE API PUBLICA" onChangeText={text => handleChange(text, "api_publica")} />
        <TextBox placeholder="INGRESE API SECRETA" onChangeText={text => handleChange(text, "api_secreta")}  />
        <View style={{ flexDirection: "row", justifyContent: "center", alignItems: "center", width: "92%", }}>
            <Btn onClick={() => Api()}  title="CONECTAR" style={{ width: "48%" }} />
            <Btn onClick={() => Keys()}  title="PRUEBA" style={{ width: "48%" }} />
            
        </View>
    </View>
}
