import React, { useState } from 'react'
import { Text, View, StyleSheet, ImageBackground,Image,TouchableOpacity, Switch ,TextInput,SafeAreaView  } from "react-native"
import * as Animatable from 'react-native-animatable';
import firebase from 'firebase/app';
import "firebase/auth";

export default function LoginScreen({ navigation }) {

    const [number,setNumber] = React.useState(null);
    const replica = Array(number).fill("")



    const styles = StyleSheet.create({
        text: {
          top:"100%",  
          height: 40,
          margin: 12,
          
          padding: 10,
        },
        input: {
          top:"105%",  
          height: 40,
          margin: 12,
          borderWidth: 1,
          padding: 10,
        },
      
        enter:{
          top:"350%",  
          height: 40,
          width:80,
          borderRadius:10,
          margin: 12,
          borderWidth: 1,
          padding: 10,
          backgroundColor:"blue",
          
        }
      });



 
    return (
        
        <View>
            <Text style={styles.text}>PISOS</Text>
            <TextInput
                style={styles.input}
                onChangeText={setNumber}
                placeholder="Ingrese numero"
                keyboardType="numeric"
                value={number}
            />
            <TouchableOpacity  onPress={() => replica()}>
                <Text style={styles.enter}>Enter</Text>
            </TouchableOpacity>
            <Text>
               {replica.map((rep ,index => {
                   return <TextInput key={index.toString()}
                   keyboardType='numeric'
                   maxLength={1}
                   style={styles.input}
                   />
               }))}
            </Text>
        
        </View>
    )
}
