import React, { useState } from 'react'
import { Text, View, StyleSheet } from "react-native"
import TextBox from "../components/TextBox"
import Btn from "../components/Btn"
import firebase from 'firebase/app';
import "firebase/auth";
import "firebase/firestore";





const styles = StyleSheet.create({
    view: {
        flex: 1,
        width: "100%",
        justifyContent: "center",
        alignItems: "center"
    }
})

export default function SignUpScreen ({ navigation }) {

    const [values, setValues] = useState({
      
        email: "",
        pwd: "",
        pwd2: ""
    })

    function handleChange(text, eventName) {
        setValues(prev => {
            return {
                ...prev,
                [eventName]: text
            }
        })
    }

    function SignUp() {

        const { email, pwd, pwd2 } = values

        if (pwd == pwd2) {
            firebase.auth().createUserWithEmailAndPassword(email, pwd)
           
            
            
            
                .then(() => {
                    
                })
                
                .catch((error) => {
                    alert(error.message)
                    // ..
                });
        } else {
            alert("Las contraseñas no coinciden!")
        }
    }
   

    return <View style={styles.view}>
        <Text style={{ fontSize: 34, fontWeight: "800", marginBottom: 20 }}>REGISTRO</Text>
        <TextBox placeholder="CORREO ELECTRONICO" onChangeText={text => handleChange(text, "email")} />
        <TextBox placeholder="CONTRASEÑA" secureTextEntry={true}  onChangeText={text => handleChange(text, "pwd")}/>
        <TextBox placeholder="CONFIRMAR CONTRASEÑA" secureTextEntry={true}  onChangeText={text => handleChange(text, "pwd2")}/>
        <View style={{ flexDirection: "row", justifyContent: "center", alignItems: "center", width: "92%", }}>
            <Btn onClick={() =>  SignUp() } title="CREAR" style={{ width: "48%" }}  />
            <Btn onClick={() => navigation.navigate("Login")} title="Iniciar sesion" style={{ width: "48%", backgroundColor: "#344869" }} />
        </View>
        
    </View>
}
