import React, { useState } from 'react'
import { Text, View, StyleSheet, ImageBackground,TouchableOpacity } from "react-native"
import TextBox from "../components/TextBox"
import Btn from "../components/Btn"
import firebase from 'firebase/app';
import "firebase/auth";
import "firebase/firestore";





const styles = StyleSheet.create({
    view: {
        flex: 1,
        width: "100%",
        justifyContent: "center",
        alignItems: "center"
    },
    fondo:{
        flex: 2,
        height:'100%',
        width: '100%',
        resizeMode:'contain',
        alignItems:'center', 
        justifyContent: "center",    
    },
    btn_sign:{

        width: "60%",
        height:'30%',
        alignItems:"center",
        justifyContent:"center",
        backgroundColor:"#FBBA00",
        borderRadius:20

    },
    text_sign:{
        fontSize: 20,
        fontWeight:'bold',
        color:"#4D1A70"
    }

})

export default function SignUpScreen ({ navigation }) {

    const [values, setValues] = useState({
      
        email: "",
        pwd: "",
        pwd2: ""
    })

    function handleChange(text, eventName) {
        setValues(prev => {
            return {
                ...prev,
                [eventName]: text
            }
        })
    }

    function SignUp() {

        const { email, pwd, pwd2 } = values

        if (pwd == pwd2) {
            firebase.auth().createUserWithEmailAndPassword(email, pwd)
           
            
            
            
                .then(() => {
                    
                })
                
                .catch((error) => {
                    alert(error.message)
                    // ..
                });
        } else {
            alert("Las contraseÃ±as no coinciden!")
        }
    }
   

    return <View  style={styles.view}>
                <ImageBackground source={require('../Img/fondo_simple.png')} style={styles.fondo}>
                    <Text 
                        style={{ fontSize: 34, fontWeight: "800", marginBottom: 20 , color:"#FBBA00"}}>REGISTRATE
                    </Text>
                    <TextBox 
                        placeholder="CORREO ELECTRONICO" onChangeText={text => handleChange(text, "email")} 
                    />
                    <TextBox 
                        placeholder="CONTRASEÃA" secureTextEntry={true}  onChangeText={text => handleChange(text, "pwd")}
                    />
                    <TextBox 
                        placeholder="CONFIRMAR CONTRASEÃA" secureTextEntry={true}  onChangeText={text => handleChange(text, "pwd2")}
                    />
                    <View style={{ flexDirection: "row", justifyContent: "center", alignItems: "center", width: "92%", }}>
                        <TouchableOpacity style={styles.btn_sign} onPress={() => SignUp()}>
                            <Text style={styles.text_sign}>CREAR</Text>
                        </TouchableOpacity>
                    </View>
                </ImageBackground> 
            </View>
}
