import React, { useState } from 'react'
import { Text, View, StyleSheet,ImageBackground } from "react-native"
import TextBox from "../components/TextBox"
import Btn from "../components/Btn"
import firebase from 'firebase/app';
import "firebase/auth";
import "firebase/firestore";


const styles = StyleSheet.create({
    view: {
        flex: 1,
        width: "100%",
        justifyContent: "center",
        alignItems: "center"
    },
    fondo:{
        flex: 2,
        height:'100%',
        width: '100%',
        resizeMode:'contain',
        alignItems:'center', 
        justifyContent: "center",    
    },
})

export default function Loginscreen({ navigation }) {

    const [values, setValues] = useState({
        email: "",
        pwd: ""
    })

    function handleChange(text, eventName) {
        setValues(prev => {
            return {
                ...prev,
                [eventName]: text
            }
        })
    }

    function Login() {

        const { email, pwd } = values
        const UID = firebase.auth().signInWithEmailAndPassword(email, pwd)
        
        
        
            .then(() => {

            })
            .catch((error) => {
                alert(error.message)
                // ..
            });
    }

    

    return <View style={styles.view}>
        <ImageBackground source={require('../Img/fondo_logo.png')} style={styles.fondo}>
        <Text style={{ fontSize: 30, fontWeight: "700", marginBottom: 20 , color: "#FBBA00" }}>ANDES BOT</Text>
        <TextBox placeholder="CORREO ELECTRONICO" onChangeText={text => handleChange(text, "email")} />
        <TextBox placeholder="CONTRASEÃ‘A" onChangeText={text => handleChange(text, "pwd")} secureTextEntry={true} />
        <View style={{ flexDirection: "row", justifyContent: "center", alignItems: "center", width: "92%", }}>
            <Btn onClick={() => Login()} title="Acceso" style={{ width: "48%"  }} />
        
            
        </View>
        </ImageBackground>
    </View>
}
